#!/usr/bin/env node
'use strict';
const meow = require('meow');
const updateReact = require('./');

const cli = meow(`
	Usage
	  $ update-react [input]

	Options
	  --foo  Lorem ipsum [Default: false]

	Examples
	  $ update-react
	  unicorns & rainbows
	  $ update-react ponies
	  ponies & rainbows
`);

updateReact(cli.input[0] || 'unicorns');
